<?php
namespace CE;class a{ public static function render($id,$params=false){echo "data-ce-anchor='$id' ";\CE\a::$id=$id;if($params!=false){if(isset($params['href'])){if($params['href']!=false){\CE\a::href($params['href']);}}else{\CE\a::href();}}else{\CE\a::href();}} public static function id($id=false){if(!$id){echo "data-ce-anchor='$id'";}else{echo "data-ce-anchor='$id'";\CE\a::$id=$id;}} public static function getId(){return \CE\a::$id;} public static function href($placeholder=false){$anchor;if(!\CE\article::isAssigned()){$anchor=\CE\Loader::getAnchor(\CE\a::getId());}else{$anchor=\CE\Loader::getAnchor(\CE\a::getId(),\CE\article::getId());}if($anchor!=null){echo 'href="'.$anchor['href'].'"';}elseif($placeholder!=false){echo 'href="'.$placeholder.'"';}else{echo 'href ="http://tudireccion.ce"';}} private static $id;}?>