<?php
namespace CE;class audio{ public static function render($id,$params=false){echo "data-ce-audio='$id' ";\CE\audio::$id=$id;if($params!=false){if(isset($params['src'])){if($params['src']!=false){\CE\audio::src($params['src']);}}else{\CE\audio::src();}}else{\CE\audio::src();}} public static function id($id=false){if(!$id){echo "data-ce-audio='$id' ";}else{echo "data-ce-audio='$id' ";\CE\audio::$id=$id;}} public static function getId(){return \CE\audio::$id;} public static function src($placeholder=false){$audio;if(!\CE\article::isAssigned()){$audio=\CE\Loader::getAudio(\CE\audio::getId());}else{$audio=\CE\Loader::getAudio(\CE\audio::getId(),\CE\article::getId());}if($audio!=null){echo 'src="'.$audio['src'].'"';}elseif($placeholder!=false){echo 'src="'.$placeholder.'"';}else{}} private static $id;}?>