<?php
namespace CE;class txt{ public static function render($id,$params=false){$richText='';if(isset($params['richtext'])){if($params['richtext']!=false){$richText='data-ce-richtext';}}else{}\CE\txt::$id=$id;echo "<span data-ce-text='$id' $richText>";if($params!=false){if(isset($params['text'])){if($params['text']!=false){\CE\txt::text($params['text']);}}else{\CE\txt::text();}}else{\CE\txt::text();}echo "</span>";} public static function id($id=false){if(!$id){echo "data-ce-text='$id'";}else{\CE\txt::$id=$id;echo "data-ce-text='$id'";}} public static function getId(){return \CE\txt::$id;} public static function text($placeholder=false){$text;if(!\CE\article::isAssigned()){$text=\CE\Loader::getText(\CE\txt::getId());}else{$text=\CE\Loader::getText(\CE\txt::getId(),\CE\article::getId());}if($text!=null){echo $text['contenido'];}elseif($placeholder!=false){echo $placeholder;}else{echo 'Escribe tu texto aquÃ­.';}} private static $id;}?>