<?php
namespace CE;class video{ public static function render($id,$params=false){echo "data-ce-video='$id' ";\CE\video::$id=$id;if($params!=false){if(isset($params['src'])){if($params['src']!=false){\CE\video::src($params['src']);}}else{\CE\video::src();}if(isset($params['poster'])){if($params['poster']!=false){\CE\video::poster($params['poster']);}}else{\CE\video::poster();}}else{\CE\video::src();\CE\video::poster();}} public static function id($id=false){if(!$id){echo "data-ce-video='$id' ";}else{echo "data-ce-video='$id' ";\CE\video::$id=$id;}} public static function getId(){return \CE\video::$id;} public static function src($placeholder=false){$video;if(!\CE\article::isAssigned()){$video=\CE\Loader::getVideo(\CE\video::getId());}else{$video=\CE\Loader::getVideo(\CE\video::getId());}if($video!=null){echo 'src="'.$video['src'].'"';}elseif($placeholder!=false){echo 'src="'.$placeholder.'"';}else{echo 'src="https://player.vimeo.com/external/261171668.sd.mp4?s=b9b655a7427dcbf4582e447f48d8b9c9e3e485c4&profile_id=165"';}} public static function poster($placeholder=false){$video;if(!\CE\article::isAssigned()){$video=\CE\Loader::getVideo(\CE\video::getId());}else{$video=\CE\Loader::getVideo(\CE\video::getId(),\CE\article::getId());}if($video!=null){echo 'poster="'.$video['poster'].'"';}elseif($placeholder!=false){echo 'poster="'.$placeholder.'"';}else{}} private static $id;}?>